mapper.select:
		SELECT * FROM (
			SELECT rownum rnum, G.* FROM (
				SELECT
					gallery_no, gallery_title, gallery_content,



				FROM gallery
				ORDER BY gallery_no DESC
			) G
		) GALLERY
		WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE gallery
SET gallery_hit = gallery_hit + 1
WHERE gallery_no = #{galleryNo}
