mapper.select:
SELECT * FROM (
	SELECT rownum rnum, A.* FROM (
		SELECT * FROM admin_board_report
		ORDER BY report_code DESC
	) A
) admin_board_report
WHERE rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum rnum, A.* FROM (
		SELECT * FROM admin_comment_report
		ORDER BY report_code DESC
	) A
) admin_comment_report
WHERE rnum BETWEEN #{startNo } AND #{endNo }
