mapper.select:
SELECT u_id, u_pw, u_name, u_gender, u_birth, u_email, u_phone, u_address, u_nick
FROM Users
WHERE u_id =#{uId }

mapper.update:
UPDATE Users
SET u_pw = #{uPw}, u_name=#{uName}, u_email = #{uEmail}, u_phone=#{uPhone},u_address=#{uAddress},u_nick=#{uNick},u_birth=#{uBirth}
WHERE u_id=#{uId}
