mapper.select:
    select count(*) from cart where 
		
		u_id = #{uId }
		
		
		ck_id = #{ckId }

SELECT c.*, i.file_name, g.g_name, g.g_price, g.del_price FROM cart c, goods_img i, goods g WHERE c.g_id = i.g_id AND i.file_type = 'main_img' AND c.g_id = g.g_id
AND c.u_id = #{uId }
AND c.ck_id = #{ckId }

mapper.insert:
    insert into cart (c_id, g_id, c_qty,
    	
		u_id
		
		
		ck_id
		
    ) values ( cart_seq.nextval, #{gId }, #{cQty },
    	
		#{uId }
		
		
		#{ckId}
		
	)

mapper.select:
    select count(*) from cart where g_id = #{gId } 
    
    	
		AND u_id = #{uId }
		
		
		AND ck_id = #{ckId }

mapper.delete:
    delete from cart where c_id = #{cId }
		
		AND u_id = #{uId }
		
		
		AND ck_id = #{ckId }
