mapper.select:
    
	SELECT * FROM (
	    SELECT ROWNUM rnum, t.* FROM (
	      SELECT g.*, i.file_name 
	    	FROM goods g, goods_img i
		    	WHERE g.g_id = i.g_id
		    	AND i.file_type = 'main_img'
		   		ORDER BY g.g_id ASC
	        ) t
	    ) GOODS
	    WHERE rnum BETWEEN #{startIdx} AND #{endIdx} + #{step}
